import { Component, OnInit} from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import {  SharedService } from '../shared/shared.service';
import { state } from '@angular/animations';
import { stateModel } from './state.model';

@Component({
  selector: 'app-state',
  templateUrl: './state.component.html',
  styleUrls: ['./state.component.scss']
})
export class StateComponent implements OnInit{
  StateDetails: any[] = [];
  lastUsedId: number = 0; // Variable to store the last used ID

  formValue !: FormGroup;
  stateModelObj   : stateModel = new stateModel();
  stateForm: any;
  state: any;
  refreshPage: any;
  stateService: any;
  router: any;
  //StateDetails: any;
  constructor (private FormBuilder: FormBuilder,
    private api :SharedService) { }

  ngOnInit(): void {
    this.getStateDetails();
    this.formValue = this.FormBuilder.group({
      countryName : ['', Validators.required],
      stateName : ['', [Validators.required, Validators.maxLength(150)]]
    })

  }



  postStateDetails(){
    debugger;
    this.stateModelObj.id = ++this.lastUsedId; // Generate new ID
    this.stateModelObj.countryName = this.formValue.value.countryName;
    this.stateModelObj.stateName = this.formValue.value.stateName;

    this.api.postState(this.stateModelObj)
    .subscribe(res=>{
      console.log(res);
      if(this.stateModelObj.stateName.length==0 || this.stateModelObj.stateName.trim()=== ''){
        alert("State Name Cannot be null")
      }
      else{
      alert("State Added Successfully")
      this.getStateDetails()
      this.formValue.reset()
    }
    },
    err=>{
      alert("something went wrong")
    })
  }


    // getStateDetails(){
    //   this.api.getstate()
    //   .subscribe(res=>{
    //     this.StateDetails = res;
    //   });
    // }

    getStateDetails() {
      this.api.getstate()
        .subscribe((res: any) => { // Add type annotation to 'res'
          const maxId = Math.max(...res.map((item: any) => item.id));
          // Update lastUsedId to ensure generated IDs are unique
          this.lastUsedId = maxId > 0 ? maxId : 0;
          this.StateDetails = res;
        });
    }

    cancel() {
      const stateNameInput = document.getElementById('stateName') as HTMLInputElement;
      stateNameInput.value = '';
      const countryNameInput = document.getElementById('countryName') as HTMLInputElement;
      countryNameInput.value = '';
      this.router.navigate(['/dashboard']); // Redirect to '/dashboard' route
    }


    // deleteState(row: any){
    //   this.api.deletestate(row.id)
    //   .subscribe((res: any)=>{
    //     alert("State Deleted Successfully");
    //     // this.getStateDetails();
    //     this.StateDetails = this.StateDetails.filter((state: any) => state.id !== row.id);
    //   console.log(this.StateDetails);

    //   })
    // }

    deleteState(id: number) {
      this.api.deletestate(id)
        .subscribe((res: any) => { // Add type annotation to 'res'
          alert("State Deleted Successfully");
          this.StateDetails = this.StateDetails.filter((state: any) => state.id !== id);

        });
    }

    editState(row: any){
      this.formValue.controls['stateName'].setValue(row.stateName);
      this.api.editState(row)
      .subscribe((res: any) => {
        alert("updated successfully");
      });

    }

}
